package com.mkpits.datatypes;

/**
 * 
 */
public class BooleanDataType {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		// Logical Boolean Operator 
		// Boolean Operator give response in True or False
		// Default value  is False
		// No space occupied in memory
		//e.g
		
		boolean isCorrect = true;
		boolean isWrong = false;
		
		System.out.println(isCorrect);
		System.out.println(isWrong);
		System.out.println("If Answer is correct:-"+isCorrect);
		System.out.println("If Answer is wrong:-"+isWrong);
	}

}
