package com.mkpits.datatypes;

public class FloatDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// By default all floating numbers in java are in double form
		// So, to represent it as a float we must have to append letter ‘F’ or ‘f’ after floating no
		// default value float= 0.0
		// memory size 4 byte
		// range --> Stores fractional numbers. Sufficient for storing 6 to 7 decimal digits
		 float nonIntegralno = 49.24f;
		 System.out.println(" variable nonIntegralno is example of floating type datatype, nonIntegralno : "+nonIntegralno);
	}

}
